package JavaPrograms;

public class Swap2Number {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int a = 10;
	        int b = 20;
	        a += b;
	        b = a - b;
	        a -= b;
	        System.out.println("a=" + a);
	        System.out.println("b=" + b);
	}

}
